mapper.delete:
DELETE
FROM tb_room
WHERE id = #{id} AND type = #{type};

mapper.update:
UPDATE tb_room
SET status = 1, start_time = #{startTime}, end_time = DATE_ADD(#{startTime}, INTERVAL #{hours} HOUR)
WHERE id = #{id} AND type = #{type}

UPDATE tb_room
SET status = 0, start_time = NULL, end_time = NULL
WHERE id = #{id} AND type = #{type}

UPDATE tb_room
SET end_time = DATE_ADD(end_time, INTERVAL #{hours} HOUR)
WHERE id = #{id} AND type = #{type}

mapper.select:
SELECT *
FROM tb_room
WHERE id = #{id} AND type = #{type}
